{
  "inputs": [
    {
      "type": "list",
      "label": "Scheduled Finding Search",
      "nodeType": "trigger",
      "name": "findingReport",
      "id": "findingReport",
      "nodeWidth": 280
    }
  ],
  "enabled": false,
  "applyOnExistingData": false,
  "cron": "0 0 7 ? * 1,2,3,4,5,6,7",
  "tasks": [
    {
      "name": "filteredFindings",
      "type": "findings/filter",
      "label": "Finding",
      "nodeType": "filter",
      "findings": "${findingReport}",
      "filters": [
        {
          "key": "newUrl",
          "operation": "MATCHES",
          "value": "https://test.com"
        }
      ]
    },
    {
      "name": "updateSeverity",
      "type": "findings/update",
      "label": "Update Finding",
      "nodeType": "updateSeverity",
      "findings": "${filteredFindings}",
      "config": {
        "addFindingTags": [
          "url:https://test.com"
        ]
      }
    },
    {
      "name": "moveFindings",
      "type": "findings/move",
      "label": "Move Finding",
      "nodeType": "moveFindings",
      "findings": "${filteredFindings}",
      "config": {
        "apId": "7694",
        "aspId": "36911",
        "aeId": 72712
      }
    }
  ],
  "timeZone": "America/Los_Angeles",
  "label": "davis_test",
  "type": "FINDING_SCHEDULE_REPORT"
}

